import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      monsters: []
    };
  }

  //Word geroepen zodra het root element bestsaat
  componentDidMount() {
    //Haal data op via api
    fetch('https://jsonplaceholder.typicode.com/users')
    //Maak er een JSON formaat van
    .then(response => response.json())
    //Voeg de teruggekeerde json data toe aan de Monsters array
    .then(users => this.setState({ monsters: users }));
  }

  //Render de DOM
  render() {
    return(
      <div className="App">
        {/* Loop door de array Monsters heen en maak een h1 aan voor ieder monster met de naam erin*/}
        {this.state.monsters.map(monsters => 
          <h1 key={monsters.id}> { monsters.name } </h1>
          )
        }
      </div>
    );
  }
}

export default App;
